import React from "react";
import { useEffect } from 'react';
import SpeechRecognition, { useSpeechRecognition } from 'react-speech-recognition'
import './sptotext.css';
class App extends React.Component {

// const Services = () => {
//   const { transcript, resetTranscript } = useSpeechRecognition()

//   useEffect(() => {
//       SpeechRecognition.startListening({ continuous: true })
//       console.log('listening starts');
//   }, []);

  constructor(props) {
    super(props);
    this.state = {
      change: true
    };
  }
  componentDidMount() {
    const { change } = this.state;
    this.setState({ change: change });
    console.log("jaymin", this.state.change);
  }
  deleteRow(e) {
    console.log("fdgfd", e);
    if (e === true) {
      this.setState({ change: false });
    } else {
      this.setState({ change: true });
    }
  }
  render() {

  return (
<>
<div class="main-block-speech">
      <h1>Speech Recognition</h1>
      <form action="/">
      <div id="grad1">
<form>
      <div >
     



          <div class="account-type">
              {/* <input value={transcript}/> */}
              <textarea value={transcript}></textarea>
          </div>
          <div class="btn-block-speech-bt-speech">

              <button onClick={resetTranscript}>Clear Text</button>
             
              <button onClick={(e) => this.deleteRow(this.state.change)}>
          Click Here!
        </button>
        {this.state.change ? (<h1></h1>) : 
            (
                <iframe
                    src="https://stackoverflow.com/"
                    width="540"
                    height="450"></iframe>
            )
        }
              <button onClick={(e) => { e.preventDefault(); SpeechRecognition.stopListening(); console.log('listening Stop...'); }}>Stop Listening</button>
          </div>
          <div class="btn-block-speech-speech">
             <hr />
              <span>
                  <p> 
                     <b>This React App Created by Ganesh Modi&#128515;</b>
                  </p>
              </span>
<hr />
              <p> <b>SpeechRecognition
                  by</b></p>
                      <div class="btn-block">
                          <h1>Jio intern</h1>
                          <p>Ganesh1.Modi@ril.com</p>
                      </div>
          </div>
      </div>
      
      </form>

      <hr />
      </div>

      </form>
    </div>
      </>
      
  )
    }
}


export default Services;
